import React from 'react';
import './App.css';
import Main1 from './Components/main';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component{
  constructor(){
    super()
    this.state={
      value2:'',
      value3:"BTC",
      value4:"LTC",
      value5:'btc',
      value6:"ltc"
    }
  }

  changevalue = ()=>{
    this.setState({
      value2:'receive'
    })
  }

  reverse1= ()=>{
    this.setState({
      value3:"LTC",
      value4:"BTC",
      value5:'ltc',
      value6:"btc"
    })
  }

  render(){
    return(
      <div>
        <Main1 change1={this.changevalue} value2 ={this.state.value2} value3={this.state.value3} value4={this.state.value4} value5={this.state.value5} value6={this.state.value6} reverse={this.reverse} />
      </div>
    )
  }
}

export default App;
