import React from 'react';
import './App.css';
import Main1 from './Components/main';
import 'bootstrap/dist/css/bootstrap.min.css';

class App extends React.Component{
  constructor(){
    super()
    this.state={
      value2:'',
      value3:"LTC",
      value4:"BTC",
      value5:'ltc',
      value6:"btc",
      value10:'lite',
      value11:'bit',
      from:'ltc',
      to:'btc'
    }
  }

  changevalue = ()=>{
    this.setState({
      value2:'receive',

    })
  }

  reverse1= ()=>{
    this.setState({
      value3:this.state.value4,
      value4:this.state.value3,
      value5:this.state.value6,
      value6:this.state.value5,
      value10:this.state.value11,
      value11:this.state.value10
    })
  }

  changetodoge1 = ()=>{
    this.setState({
      value3:'DOGE',
      value5:'doge',
      value10:'doge',
      from:'doge',
    })
  }

  render(){
    return(
      <div>
        {this.state.from=='ltc'
        ?<div>
        <Main1 change1={this.changevalue} value2 ={this.state.value2} value3={this.state.value3} value4={this.state.value4} value5={this.state.value5} value6={this.state.value6} reverse={this.reverse1} value10={this.state.value10} value11={this.state.value11} changetodoge={this.changetodoge1} />
        </div>
        :<div>
          {this.state.from=='doge'
          ?<div>
                    <Main1 change1={this.changevalue} value2 ={this.state.value2} value3={this.state.value3} value4={this.state.value4} value5={this.state.value5} value6={this.state.value6} reverse={this.reverse1} value10={this.state.value10} value11={this.state.value11} changetodoge={this.changetodoge1} />
          </div>
          :<div></div>
          }
        </div>
      }
      </div>
    )
  }
}

export default App;
